CREATE FUNCTION customDateFunction(inputDate DATE) RETURNS DATE AS
BEGIN
  DECLARE outputDate DATE;

  IF (MONTH(inputDate) BETWEEN 2 AND 7) THEN
    -- –ï—Å–ª–∏ –¥–∞—Ç–∞ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –º–µ–∂–¥—É 1 —Ñ–µ–≤—Ä–∞–ª—è –∏ 31 –∏—é–ª–µ–º, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º 1 —è–Ω–≤–∞—Ä—è —Å–ª–µ–¥—É—é—â–µ–≥–æ –≥–æ–¥–∞
