–î–∞, —è –º–æ–≥—É –ø–æ–º–æ—á—å –≤–∞–º –≤ —ç—Ç–æ–º. –û–¥–Ω–∞–∫–æ, –ø–æ—Ç—Ä–µ–±—É–µ—Ç—Å—è –±–∏–±–ª–∏–æ—Ç–µ–∫–∞ <fstream> –¥–ª—è —á—Ç–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∏–∑ —Ñ–∞–π–ª–∞ –∏ –±–∏–±–ª–∏–æ—Ç–µ–∫–∞ <string> –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å–æ —Å—Ç—Ä–æ–∫–∞–º–∏. –ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞, –∫–æ—Ç–æ—Ä—ã–π –≤—ã–ø–æ–ª–Ω—è–µ—Ç —Ç–æ, —á—Ç–æ –≤—ã —Ö–æ—Ç–∏—Ç–µ:

```cpp
#include <fstream>
#include <sstream>
#include <string>
#include <iostream>

int main() {
    std::string wordToFind;
    std::cout << "–í–≤–µ–¥–∏—Ç–µ —Å–ª–æ–≤–æ –¥–ª—è –ø–æ–∏—Å–∫–∞: ";
    std::cin >> wordToFind;

    std::ifstream file("filename.txt");

    if(!file.is_open()) {
        std::cout << "–ù–µ —É–¥–∞–ª–æ—Å—å –æ—Ç–∫—Ä—ã—Ç—å —Ñ–∞–π–ª." << std::endl;
        return 1;
    }

    int count = 0;
    std::string line;

    while(getline(file, line)) {
        std::istringstream iss(line);
        std::string word;

        while(iss >> word) {
            // –µ—Å–ª–∏ –Ω–∞–π–¥–µ–Ω–Ω–æ–µ —Å–ª–æ–≤–æ —Å–æ–≤–ø–∞–¥–∞–µ—Ç —Å –∏—Å–∫–æ–º—ã–º, —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫
            if(word == wordToFind) {
